<script>
  import { addProduct } from "../../../stores/products";
  import { scale } from "svelte/transition";
  import { v4 } from "uuid";

  export let showProductFormModal;
  let imgPreview, fileInput;

  let name;
  let price;
  let link;
  let description;

  const registerProduct = () => {
    getDataURLOfImage()
      .then((dataURL) => {
        const newProduct = {
          code: "#" + v4(),
          img: dataURL,
          name,
          price,
          link,
          description,
        };

        addProduct(newProduct);
        showProductFormModal = false;
      })
      .catch(() => {
        alert("상품을 등록하는 도중 문제가 발생했습니다.");
      });
  };

  function getDataURLOfImage() {
    return new Promise((resolve, reject) => {
      let img = fileInput.files[0];

      let reader = new FileReader();
      reader.readAsDataURL(img);

      reader.onloadend = (e) => {
        resolve(e.target.result);
      };

      reader.onerror = (err) => {
        reject(err);
      };
    });
  }

  const fileSelected = (e) => {
    console.log(getDataURLOfImage());
    getDataURLOfImage()
      .then((dataURL) => {
        imgPreview = dataURL;
      })
      .catch((err) => {
        alert(err);
      });
  };
</script>

<div
  class="modal-bg"
  on:click|self={() => {
    showProductFormModal = !showProductFormModal;
  }}
>
  <modal id="product-create-modal" transition:scale>
    <form class="product-form" on:submit|preventDefault={registerProduct}>
      <div class="top">
        <div class="image-preview">
          {#if imgPreview}
            <img src={imgPreview} alt="" on:click={fileInput.click()} />
          {:else}
            <img
              class="image-preview-default"
              src="data:image/png;base64,"
              on:click={fileInput.click()}
            />
          {/if}
        </div>
        <div class="name-price">
          <input
            class="name"
            type="text"
            placeholder="상품 이름"
            bind:value={name}
          />
          <div class="price-wrapper">
            <input class="price" wtype="number" bind:value={price} />
            <span>원</span>
          </div>
          <input type="text" placeholder="url" bind:value={link} />
          <input
            type="file"
            bind:this={fileInput}
            on:change={fileSelected}
            accept="image/*"
            hidden
          />
        </div>
      </div>
      <div class="mid">
        <textarea
          placeholder="상품에 대한 설명을 입력하세요"
          name=""
          id=""
          cols="30"
          rows="10"
          bind:value={description}
        />
        <button>등록하기</button>
      </div>
    </form>
  </modal>
</div>

