      gap: 2em;
      input,
      textarea {
        background-color: var(--bg-color);
        height: 4em;
        text-align: center;
      }

      textarea {
        text-align: start;
        padding: 1em;
        height: 10em;
      }
      .top {
        grid-area: top;
        display: grid;
        grid-template-areas: "image  name-price";
        grid-template-rows: $product-image-width;
        gap: 1em;
        .image-preview {
          grid-area: image;
          width: $product-image-width;
          height: $product-image-width;
          display: flex;
          align-items: center;
          justify-content: center;
          overflow: hidden;
          background-color: black;
          img {
            width: $product-image-width;
            object-fit: cover;
            &:hover {
              cursor: pointer;
            }
          }
        }

        .name-price {
          grid-area: name-price;
          display: flex;
          flex-direction: column;
          gap: 2em;

          .price-wrapper {
            background-color: var(--bg-color);
            border-radius: 1em;
            display: flex;
            align-items: center;
            input.price {
              text-align: right;
              grid-area: price;
            }
